import React from "react";
import './popup.css'


const Popup = (props) => {
const {id, name, url, count, shops} = props
const setShops = props.setShops

const removeToCart = () => {
    const cart = shops.map((el,idx)=>
    idx===(id-1)
        ? el = {id, name, url, count:0}
        :el)
    setShops(cart)
}
    return(
            <li className="popup-li collection-item avatar">
                {console.log(id, name, url, count)}
            <div className="row">
            <div className="col s10">
                <img src={url} alt="" className="circle"></img>
                <p className="black-text"><strong className="label">Title: </strong>{name}</p>
                <p className="black-text"><strong className="label">count: </strong></p>
            </div>
            <div className="col s2">    
                <button onClick={()=>{
                    removeToCart()}}
                    className="waves-effect  waves-light btn" 
                    >
                    <i className="material-icons window-popup">delete_forever</i>    
                </button>
            </div>
            </div>
            </li>
    )
}

export default Popup;
