import React, {useState}  from "react";
import { Link } from "react-router-dom";
import Shop from '../Shop'
import './product.css'

const Product = (props) => {
    const {id, thumbnailUrl, title, shop} = props
    const [open, isOpen] = useState(true)

    return (
        <div key={`key${id}`} className="col s3 ">
            <div className="card post-card">
            <Link key={id} to={`${id-1}`}>
                    <img 
                        className="post-img" 
                        key={id+title} 
                        alt={id} 
                        src={thumbnailUrl}>
                    </img>
                    <p 
                        className="title-post" 
                        key={id}>
                        {title}
                    </p>
            </Link>
            {open &&
            <button 
                onClick={()=>isOpen(!open)}
                className="btn shop-btn">Shop
            </button>}
            {!open &&
            shop.filter(shop=>shop.ID===id)
            .map(count=><Shop key={count.ID} isOpen={isOpen} open={open} {...count}/>)}
        </div>
        </div>
    )
}

export default Product;
