import React, {useState} from "react";
import { Link } from "react-router-dom";
import Popup from '../Popup'

const Navbar = (props)=>  {
  const [toggle, setToggle] = useState(false)
  const posts = props.data

  if(!posts) return null;
return(
  <nav>
    <div className="nav-wrapper">
      <a href="/" className="brand-logo">Logo</a>
      <ul id="nav-mobile" className="right hide-on-med-and-down">
      {toggle && (
        <li key="0"><ul className="collection z-depth-3">{
          posts
          .filter(post=>post.id<4)
          .map(post=><Popup{...post}/>)
          }</ul>
        </li>)}
        <li>
          <Link onClick={()=> setToggle(!toggle)} to="/shop"> 
            <i className="material-icons">shopping_cart</i>{toggle}
          </Link>
        </li>
        <li key="1"><Link to="/">Main</Link></li>
        <li key="2"><Link to="/cat">Catalogue</Link></li>
      </ul>
    </div>
  </nav>
)
}

export default Navbar;
