import React, {useState}  from "react";
import { Link } from "react-router-dom";
import './product.css'

const Product = (props) => {
    const {id, thumbnailUrl, title} = props

    const [open, isOpen] = useState(true)

    return (
        <div key={id} className="col s3 ">
                <div className="card post-card">
            <Link key={id} to={`${id-1}`}>
                    <img className="post-img" key={id+title} alt={id} src={thumbnailUrl}></img>
                    <p className="title-post" key={id}>{title}</p>
            </Link>{open &&
                <button 
                    onClick={()=>isOpen(!open)}
                    className="btn shop-btn">Shop</button>}
                </div>
        </div>
    )
}

export default Product;
