import React, {useState, useEffect} from "react";
import Navbar from "../components/Navbar";
import {ProductPage, MainPage, CataloguePage, ShopPage } from "../pages";
import './app.css'

import { fetchData } from "../services/services-workers";

import { Route, Routes } from "react-router-dom";


const url = 'https://jsonplaceholder.typicode.com/photos'
const urlLocal = 'http://localhost:5000/'

const App = () => {
const [post, setPost] = useState(null)
const [shop, setShop] = useState("")
// const increment = () => {setCount(counts+1)}
// const decrement = () => {setCount(counts-1)}

const initialCounters = []

const [counts, setCounts] = useState(initialCounters)

const addCounter = () => {
    const newCount = {
        id: 0,
        value: 0
    }

    setCounts([...counts, newCount])
}

const plus = (id) => {
    const updateCounts = counts.map(el=> el.id === id
        ? {...el, value: el.value + 1}
        : el)
        setCounts(updateCounts)
}

useEffect(()=>{
    fetchData(urlLocal, setShop)
}, [setShop])


    useEffect(()=>{
        fetchData(url, setPost)
    }, [])

    return (
        <div className="app-main">
            <Navbar data={post} shop={shop}  counts={counts}/>
            <ul>
                {counts.map(el=> (
                    <li key={el.id}>
                        <button onClick={()=> plus(el.id)}>Plus</button>
                        {el.value}
                    </li>
                ))}
            </ul>
            <button onClick={addCounter}>Add</button>
            <div className="container white-text">
                <Routes>
                    <Route path="/" element={<MainPage />}/>
                    <Route path="/cat" element={<CataloguePage shop={shop}   counts={counts} data={post}/>}/>
                    <Route path="/cat/:id" element={<ProductPage data={post} />}/>
                    <Route path="/shop" element={<ShopPage counts={counts} shop={shop}/>}/>
                </Routes>
            </div>
        </div>
    )
}

export default App;
