import React, {useState} from 'react'
import axios from 'axios'
// import { addData } from '../../services/services-workers'

const currentURL = 'http://localhost:5000/add/1'

const Shop = (props) => {
    const [open, isOpen] = useState(true)
    const {counts, setCounts, data, plus, addCounter} = props
    const {name, url} = data



    return(
            <div >
            {/* {console.log(name, url)} */}
            {/* BUTTTON SHOP */}
            {open &&
            <button 
                onClick={()=>{
                    isOpen(!open)
                    addCounter()
                }}
                className="btn shop-btn">Shop
            </button>
            }{!open &&
                
        <div className="wrap-shop">
            <button 
                className="waves-effect col s4 waves-light btn" 
                onClick={plus(counts.id)}
                >
            <i className="material-icons">add</i>    
            </button>
            <span className="col s4 blue-text text-lighten-3 count-shop">
                {counts[0].value}
            </span>
            <button 
                className="waves-effect col s4 waves-light btn" 
                >
                <i className="material-icons">remove</i>    
            </button>
        </div> 
        }
        </div>
    )
}

export default Shop;
