import React, {useState} from 'react'
import axios from 'axios'
// import { addData } from '../../services/services-workers'

const currentURL = 'http://localhost:5000/add/1'

const Shop = (props) => {
    const [open, isOpen] = useState(true)
    const {counts, setCounts, data} = props
    const {name, url} = data

    const plus = (id) => {
        const updateCounts = counts.map(el=> el.id === id
            ? {...el, value: el.value + 1}
            : el)
            setCounts(updateCounts)
    }

const addCounter = () => {
    const newCount = {
        id: Math.random(),
        value: 1
    }

    setCounts([...counts, newCount])
}

    return(
            <div >
            {console.log(name, url)}
            {/* BUTTTON SHOP */}
            {open &&
            <button 
                onClick={()=>{
                    isOpen(!open)
                    addCounter()
                }}
                className="btn shop-btn">Shop
            </button>
            }{!open &&
                
        <div className="wrap-shop">
            <button 
                className="waves-effect col s4 waves-light btn" 
                // onClick={plus()}
                >
            <i className="material-icons">add</i>    
            </button>
            <span className="col s4 blue-text text-lighten-3 count-shop">
                {counts}
            </span>
            <button 
                className="waves-effect col s4 waves-light btn" 
                >
                <i className="material-icons">remove</i>    
            </button>
        </div> 
        }
        </div>
    )
}

export default Shop;
