import React, {useState}  from "react";
import { Link } from "react-router-dom";
import Shop from '../Shop'
import './product.css'

const Product = (props) => {
    const {id, thumbnailUrl, title, shop} = props
    // const [open, isOpen] = useState(false)

    return (
        <div key={`key${id}`} className="col s3 ">
            <div className="card post-card">
            <Link key={id} to={`${id-1}`}>
                    <img 
                        className="post-img" 
                        key={id+title} 
                        alt={id} 
                        src={thumbnailUrl}>
                    </img>
                    <p 
                        className="title-post" 
                        key={id}>
                        {title}
                    </p>
            </Link>
            {!open ?
            shop.filter(shop=>shop.id===id)
            .map(count=><Shop key={count.id} isOpen={isOpen} open={open} {...count}/>):<div>empty</div>
            }
        </div>
        </div>
    )
}

export default Product;
