import React, {useState}  from "react";
import { Link } from "react-router-dom";
import './product.css'

const Product = (props) => {
    const {id, thumbnailUrl, title, shops} = props

    const [open, isOpen] = useState(true)
    const [count, setCount] = useState(1)

    return (
        <div key={`key${id}`} className="col s3 ">
            <div className="card post-card">
            <Link key={id} to={`${id-1}`}>
                    <img 
                        className="post-img" 
                        key={id+title} 
                        alt={id} 
                        src={thumbnailUrl}>
                    </img>
                    <p 
                        className="title-post" 
                        key={id}>
                        {title}
                    </p>
            </Link>
            {open &&
            <button 
                onClick={()=>isOpen(!open)}
                className="btn shop-btn">Shop
            </button>}
            {!open && 
            <div className="wrap-shop">
                <button 
                    className="waves-effect col s4 waves-light btn" 
                    onClick={()=>setCount(count+1)}>
                <i className="material-icons">add</i>    
                </button>
                <span className="col s4 blue-text text-lighten-3 count-shop">{(count>0) && count}</span>
                <button 
                    className="waves-effect col s4 waves-light btn" 
                    onClick={()=>(count>1)?setCount(count-1):isOpen(!open)}>
                    <i className="material-icons">remove</i>    
                </button> 
            </div>}
        </div>
        </div>
    )
}

export default Product;
