import React, {useState}  from "react";
import { Link } from "react-router-dom";
import Shop from '../Shop'
import './product.css'

const Product = (props) => {
    const [open, isOpen] = useState(true)
    const {id, url, name, counts, setCounts} = props


    const plus = (id) => {
        const newCount = counts.map((el, idx)=> idx=== id? el+1 : el)
        setCounts(newCount)
    }


    const increment = plus(id)
    return (
        <div key={`key${id}`} className="col s3 ">
            <div className="card post-card">
            <Link key={id} to={`${id-1}`}>
                    <img 
                        className="post-img" 
                        key={id+name} 
                        alt={id} 
                        src={url}>
                    </img>
                    <p 
                        className="title-post" 
                        key={id}>
                        {name}
                    </p>
            </Link>
            {/* <button onClick={increment}>add</button> {counts[id]} */}
            {/* {console.log(counts[id])} */}
            {/* {open &&
            <button 
                onClick={()=>{
                    isOpen(!open)
                }}
                className="btn shop-btn">Shop
            </button>
            }{!open &&
                
        <div className="wrap-shop">
            <button onClick={plus(id)}
                className="waves-effect col s4 waves-light btn" 
                >{console.log(counts)}
            <i className="material-icons">add</i>    
            </button>
            <span className="col s4 blue-text text-lighten-3 count-shop">
                {counts[id]}
            </span>
            <button 
                className="waves-effect col s4 waves-light btn" 
                >
                <i className="material-icons">remove</i>    
            </button>
        </div> } */}
        </div>
        </div>
    )
}

export default Product;
