import React, {useState}  from "react";
import { Link } from "react-router-dom";
import './product.css'

const Product = (props) => {
    const {id, url, name, shops, shop, setShop} = props
    const [open, isOpen] = useState(true)

    const addToCart = () => {
        const cart = shops.map((el,idx)=>
        idx===(id-1)
            ? el = {id, name, url, count:1, productid:id}
            :el)
        setShop(cart)
    }

    // const removeToCart = () => {
    //     const cart = shops.map((el,idx)=>
    //     idx===(id-1)
    //         ? el = {id, name, url, count:0, productid:id}
    //         :el)
    //     setShop(cart)
    // }

    const currentData = {id, name, url, count:0, productid:id}

const remove = (arr, data, setFunc) => {
    const cart = arr.map((el,idx)=> 
        idx===(data.id-1)
            ? el = {data}
            : el)
        setFunc(cart)
}



    return (
        <div key={`key${id}`} className="col-s3">
            <div className="card post-card">
            <Link key={id} to={`${id-1}`}>
                    <img 
                        className="post-img" 
                        key={id+name} 
                        alt={id} 
                        src={url}>
                    </img>
                    <p 
                        className="title-post" 
                        key={id}>
                        {name}
                    </p>
            </Link>
            {!Boolean(shop['count']) ?
                <div><button 
                    onClick={()=>{
                        isOpen(!open)
                        addToCart()
                    }}
                    className="btn shop-btn">SHOP
                        
                </button>
                </div>:
                <div className="wrap-shop">
                <span className="col s4 blue-text text-lighten-3 count-shop">
                    {shops.id}
                </span>
                <button onClick={()=>{
                    isOpen(!open) 
                    remove(shops, currentData, setShop)}}
                    className="waves-effect col s4 waves-light btn" 
                    >
                    <i className="material-icons">delete_forever</i>    
                </button>
                </div>}
        </div>
    </div>
    )
}

export default Product;
