import React, {useState} from 'react'

const Shop = (props) => {
    // const [open, isOpen] = useState(false)
    const {id, name, url, count, isOpen, open} = props
    const [counts, setCount] = useState(id)
    return(
        <div>
            {open &&
            <button 
                onClick={()=>isOpen(!open)}
                className="btn shop-btn">Shop
            </button>}
{/* --------------------------------- */}
            {!open &&
        <div className="wrap-shop">
            {console.log(name, url)}
            <button 
                className="waves-effect col s4 waves-light btn" 
                onClick={()=>setCount(counts+1)}>
            <i className="material-icons">add</i>    
            </button>
            <span className="col s4 blue-text text-lighten-3 count-shop">{counts>0 && counts}</span>
            <button 
                className="waves-effect col s4 waves-light btn" 
                onClick={()=>counts>1 ? setCount(counts-1):isOpen(!open)}>
                <i className="material-icons">remove</i>    
            </button> 
        </div>}
        </div>
    )
}

export default Shop;
