import React, {useState}  from "react";
import { Link } from "react-router-dom";
import Shop from '../Shop'
import './product.css'

const Product = (props) => {
    const {id, url, name, shops, setShop} = props
    const [open, isOpen] = useState(true)

    const addToCart = () => {
        const cart = shops.map((el,idx)=>idx===id-1?el.count+1:el)
        setShop(cart)
    }

    return (
        <div key={`key${id}`} className="col-s3">
            <div className="card post-card">
                {console.log(shops)}
            <Link key={id} to={`${id-1}`}>
                    <img 
                        className="post-img" 
                        key={id+name} 
                        alt={id} 
                        src={url}>
                    </img>
                    <p 
                        className="title-post" 
                        key={id}>
                        {name}
                    </p>
            </Link>
            {open &&
            <button 
                onClick={()=>{
                    isOpen(!open)
                    addToCart()
                }}
                className="btn shop-btn">{shops.name}
            </button>
            }
            {!open &&
            <div className="wrap-shop">
            <span className="col s4 blue-text text-lighten-3 count-shop">
                {shops.id}
            </span>
            <button onClick={()=>isOpen(!open)}
                className="waves-effect col s4 waves-light btn" 
                >
                <i className="material-icons">delete_forever</i>    
            </button>
            </div> }
        </div>
    </div>
    )
}

export default Product;
