import React from "react";
import Product from './../components/Product'


const CataloguePage = (props) => {
    const {counts, setCounts, shop} = props

    const plus = (id) => {
        const updateCounts = counts.map(el=> el.id === id
            ? {...el, value: el.value + 1}
            : el)
            setCounts(updateCounts)
    }

    const addCounter = () => {
        const newCount = {
            id: 0,
            value: 1
        }

    setCounts([...counts, newCount])
}

    if(!shop) return null;
    return(
    <div className="row">
        <div className="fields">{
        shop.map(shop=>
            <Product 
                counts={counts} 
                setCounts={setCounts}
                key={shop.id} 
                {...shop}/>
        )}
        {counts.map(el => 
            <div>{el.id}</div>
            )}
        </div>
    </div>
    )
}

export default CataloguePage;
