import React, {useState}  from "react";
import { Link } from "react-router-dom";
import Shop from '../Shop'
import './product.css'

const Product = (props) => {
    const [open, isOpen] = useState(true)
    const {id, url, name} = props

    const arr = () => {
        let newArr = []
        for(let i = 0; i<12; i++) {
           newArr.push(0)
        }
        return newArr
    }

   const addToCart = (id) => {
    const Arr = arr()
    Arr.map((el, idx)=>idx === id ? el+1:el)
    return console.log(Arr)
   }

    return (
        <div key={`key${id}`} className="col s3 ">
            <div className="card post-card">
            <Link key={id} to={`${id-1}`}>
                    <img 
                        className="post-img" 
                        key={id+name} 
                        alt={id} 
                        src={url}>
                    </img>
                    <p 
                        className="title-post" 
                        key={id}>
                        {name}
                    </p>
            </Link>
            {open &&
            <button 
                onClick={()=>{
                    isOpen(!open)
                    arr()[id-1] = 1
                    console.log(arr)
                    addToCart(id)
                }}
                className="btn shop-btn">Shop
            </button>
            }
            {!open &&
            <div className="wrap-shop">
            <span className="col s4 blue-text text-lighten-3 count-shop">
                {id}
            </span>
            <button onClick={()=>isOpen(!open)}
                className="waves-effect col s4 waves-light btn" 
                >
                <i className="material-icons">delete_forever</i>    
            </button>
            </div> }
        </div>
    </div>
    )
}

export default Product;
