import React, {useState} from "react";
import Product from './../components/Product'


const CataloguePage = (props) => {
    const {shop} = props


    const initialCounters = []

    const [counts, setCounts] = useState(initialCounters)

    const plus = (id) => {
        const updateCounts = counts.map(el=> el.id === id
            ? {...el, value: el.value + 1}
            : el)
            setCounts(updateCounts)
    }

    const addCounter = () => {
        const newCount = {
            id: 0,
            value: 1
        }

    setCounts([...counts, newCount])
}

    if(!shop) return null;
    return(
    <div className="row">
    {addCounter}
    {counts[0].id}
        <div className="fields">{
        shop.map(shop=>
            <div key={shop.id}>
            <Product 
                counts={counts} 
                setCounts={setCounts}
                key={shop.id} 
                {...shop}/>
            <div>
                
            </div>
            </div>
        )}
        </div>
    </div>
    )
}

export default CataloguePage;
