import React, {useState, useEffect} from "react";
import Navbar from "../components/Navbar";
import {ProductPage, MainPage, CataloguePage, ShopPage } from "../pages";
import './app.css'
import axios from "axios";

import { Route, Routes } from "react-router-dom";


const localUrl = 'http://localhost:5000/shop'
const url = 'https://jsonplaceholder.typicode.com/photos'

const App = () => {
const [post, setPost] = useState(null)
const [shop, setShop] = useState(null)

    useEffect(()=>{
        axios.get(url).then((res)=>{
            setPost(res.data)
        })
    }, [])


    const shopDetail = async ()=> {
        const res = await axios.get(localUrl, {
                headers: {
                    'Access-Control-Allow-Origin' : '*',
                    'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE,PATCH,OPTIONS'
                    }
            })
            setShop(res.data[0].id)
    }
    useEffect(()=>{
        shopDetail()
    }, [shop])

    return (
        <div className="app-main">
            <Navbar test={post} count={shop} data={post}/>
            <div className="container white-text">
                {shop}
                <Routes>
                    <Route path="/" element={<MainPage />}/>
                    <Route path="/cat" element={<CataloguePage data={post}/>}/>
                    <Route path="/cat/:id" element={<ProductPage data={post} />}/>
                    <Route path="/shop" element={<ShopPage/>}/>
                </Routes>
            </div>
        </div>
    )
}

export default App;
