import React, {useState, useEffect} from "react";
import Navbar from "../components/Navbar";
import {ProductPage, MainPage, CataloguePage, ShopPage } from "../pages";
import './app.css'

import axios from "axios";

import { Route, Routes } from "react-router-dom";


const url = 'https://jsonplaceholder.typicode.com/photos'
const urlLocal = 'http://localhost:5000/'

const App = () => {
// const [post, setPost] = useState(null)
const [shop, setShop] = useState(null)



fetch(urlLocal).then((res)=>{
        setShop(res.data)
    }).catch(err=>console.log(err))

    return (
        <div className="app-main">
            <Navbar  shop={shop} />
            <div className="container white-text">
                <Routes>
                    <Route path="/" element={<MainPage />}/>
                    <Route path="/cat" element={<CataloguePage shop={shop} />}/>
                    <Route path="/cat/:id" element={<ProductPage />}/>
                    <Route path="/shop" element={<ShopPage  shop={shop}/>}/>
                </Routes>
            </div>
        </div>
    )
}

export default App;
