import React, {useState, useEffect} from 'react'
import axios from 'axios'

const Shop = (props) => {
    // const [open, isOpen] = useState(false)
    const {name, url, open, isOpen} = props
    let count = props.count
    const [counts, setCount] = useState(count)



    return(
        <div>
            {!open &&
        <div className="wrap-shop">
            {console.log(name, url)}
            <button 
                className="waves-effect col s4 waves-light btn" 
                onClick={()=> {
                        axios.post('http://localhost:5000/add/1', {
                            headers: {
                                'Access-Control-Allow-Origin' : '*',
                                'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE,PATCH,OPTIONS'
                              }
                        })
                            .catch((err)=> {
                                console.log("error")
                            })
                            setCount(counts++)

                }}
                >
            <i className="material-icons">add</i>    
            </button>
            <span className="col s4 blue-text text-lighten-3 count-shop">
                {counts}
            </span>
            <button 
                className="waves-effect col s4 waves-light btn" 
                onClick={()=>counts>1 ? setCount(counts-1):isOpen(!open)}>
                <i className="material-icons">remove</i>    
            </button> 
        </div>}
        </div>
    )
}

export default Shop;
