import React from "react";
import './popup.css'


const Popup = (props) => {
const {id, name, url, count, shops} = props
const counts = props.counts
const setShops = props.setShops

const removeToCart = () => {
    const cart = shops.map((el,idx)=>
    idx===(id-1)
        ? el = {id, name, url, count:0, productid:id}
        :el)
    setShops(cart)
}
    return(
            <li className="popup-li collection-item avatar">
                {console.log(id, name, url, count)}
            <div>
                <img src={url} alt="" className="circle"></img>
                <p className="black-text"><strong className="label">Title: </strong>{name}</p>
                <p className="black-text"><strong className="label">count: </strong>{counts}</p>
            </div>
            </li>
    )
}

export default Popup;
