import React, {useState}  from "react";
import { switchToCart } from "../../services/services-workers";
import './unit.css'

const Unit = (props) => {
    const {id, count, name, url, title, shops, setShops} = props
    const [open, isOpen] = useState(true)
    const incrementData = {id, name, url, count:1}
    const decrementData = {id, name, url, count:0}
    
    return (
    <div className="fields">
        <img id="post" className="post-img" alt={id} src={url}></img>
        <p>{title}</p>
        
        
        {!Boolean(count) ?
    <div><button 
        onClick={()=>{
            isOpen(!open)
            switchToCart(shops, incrementData, setShops)
        }}
        className="btn shop-btn">SHOP
            
    </button>
    </div>:
    <div className="wrap-shop">
    <span className="col s4 blue-text text-lighten-3 count-shop">
        {shops.id}
    </span>
    <button onClick={()=>{
        isOpen(!open) 
        switchToCart(shops, decrementData, setShops)}}
        className="waves-effect col s4 waves-light btn" 
        >
        <i className="material-icons">delete_forever</i>    
    </button>
    </div>}


    </div>
    )
}

export default Unit;
