import React, {useState} from 'react'

const Shop = (props) => {
    // const [open, isOpen] = useState(false)
    const {ID, NAME, IMG, isOpen, open} = props
    const [count, setCount] = useState(ID)
    return(
        <div>{!open &&
        <div className="wrap-shop">
            {console.log(NAME, IMG)}
            <button 
                className="waves-effect col s4 waves-light btn" 
                onClick={()=>setCount(count+1)}>
            <i className="material-icons">add</i>    
            </button>
            <span className="col s4 blue-text text-lighten-3 count-shop">{count>0 && count}</span>
            <button 
                className="waves-effect col s4 waves-light btn" 
                onClick={()=>count>1 ? setCount(count-1):isOpen(!open)}>
                <i className="material-icons">remove</i>    
            </button> 
        </div>}
        </div>
    )
}

export default Shop;
